import React, { Component } from "react";
import { connect } from 'react-redux';
import ShelfHeader from "./ShelfHeader/index";
import ProductList from "./ProductList/index";
import PropTypes from 'prop-types';
import Data from "./data.json";
import "./style.scss"
import Spinner from './Spinner';
import { fetchProducts } from '../../reducers/reducer';

class Shelf extends Component {
    // static propTypes = {
    //     fetchProducts: PropTypes.func.isRequired,
    //     products: PropTypes.array.isRequired,
    //     filters: PropTypes.array,
    //   };
    // constructor(props) {
    //     super(props);
    //     this.state = {
    //         isLoading: false,
    //         products:[],
    //     };
    // };

    // componentDidMount() {
    //     this.handleFetchProducts();
    // }

    // componentWillReceiveProps(nextProps) {
    //     const { filters: nextFilters } = nextProps;

    //     if (nextFilters !== this.props.filters) {
    //         this.handleFetchProducts(nextFilters, undefined);
    //     }
    // }

    // handleFetchProducts = (
    //     filters = this.props.filters,
    // ) => {
    //     this.setState({ isLoading: true });
    //     this.props.fetchProducts(filters, () => {
    //         this.setState({ isLoading: false });
    //     });
    // };

    state = {
        value1: 'ALL',
    };

    constructor(props) {
        super(props);
        this.handleChange = this.handleChange.bind(this)
        this.checksize = this.checksize.bind(this)

    }

    handleChange = event => {
        this.setState({ value1: event.target.value });
    };

    checksize(products) {

        let temp = this.state.value1;
        if (temp === 'ALL') return products
        else
        return products.availableSizes.indexOf(temp) > -1

    }

    render(){
        // const { products } = this.state.products;
        // const { isLoading } = this.state;
        return (
            <React.Fragment>
                {/* {isLoading && <Spinner />} */}
                <div className="shelf-container">
                <RadioGroup
              aria-label="Size"
              name="Size"
            //   className={classes.group}
              value={this.state.value1}
              onChange={this.handleChange}
            >
              <FormControlLabel value="ALL" control={<Radio />} label="ALL" />
              <FormControlLabel value="XS" control={<Radio />} label="XS" />
              <FormControlLabel value="S" control={<Radio />} label="S" />
              <FormControlLabel value="M" control={<Radio />} label="M" />
              <FormControlLabel value="ML" control={<Radio />} label="ML" />
              <FormControlLabel value="L" control={<Radio />} label="L" />
              <FormControlLabel value="XL" control={<Radio />} label="XL" />
              <FormControlLabel value="XXL" control={<Radio />} label="XXL" />

            </RadioGroup>
                    {/* < ShelfHeader len={Object.keys(Data.products).length}/> */}
                    < ProductList data={Data}/>
                </div>
            </React.Fragment>
        );
    }
}

// const mapStateToProps = state => ({
//     products: state.reducer.products,
//     filters: state.reducer.items,
// });

// export default connect(
//     mapStateToProps,
//     { fetchProducts }
// )(Shelf);

export default Shelf;
