import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import Thumb from '../../../Thumb';

import { formatPrice } from '../../../../services/util';
import { addProduct } from '../../../../reducers/reducer';

const Product = ({ item, addProduct }) => {


  let productInstallment;
    let formattedPrice = formatPrice(item.price, item.currencyId);

    prod.quantity = 1;

    if (!!item.installments) {
      const installmentPrice = item.price / item.installments;

      productInstallment = (
        <div className="installment">
          <span>or {item.installments} x</span>
          <b>
            {item.currencyFormat}
            {formatPrice(installmentPrice, item.currencyId)}
          </b>
        </div>
      );
    }

    return (
        <div className="shelf-item">
          {item.isFreeShipping && (
          <div className="shelf-stopper">Free shipping</div>)}
          <Thumb classes="shelf-item__thumb" src={item} alt={item.title}/>
          <p className="shelf-item__title">{item.title}</p>
          <div className="shelf-item__price">
            <div className="val">
              <small>{item.currencyFormat}</small>
              <b>{formattedPrice.substr(0, formattedPrice.length - 3)}</b>
              <span>{formattedPrice.substr(formattedPrice.length - 3, 3)}</span>
            </div>
            {productInstallment}
          </div>
          <div className="shelf-item__buy-btn" onClick={() => addProduct(item)}>Add to cart</div>
        </div>
      );
};

Product.propTypes = {
  product: PropTypes.object.isRequired,
  addProduct: PropTypes.func.isRequired
};

export default connect(
  null,
  { addProduct }
)(Product);
